import { useState, useEffect } from "react";
import ReactApexChart from "react-apexcharts";
import axios from "axios";

const Charts = () => {
  const [data, setData] = useState([]);
  const [table, setTable] = useState([]);
  const [dizi, setDizi] = useState([]);
  const [series, setSeries] = useState([]);
  const [serie, setSerie] = useState([]);
  const fetchData = async () => {
    try {
      const response = await axios.get("http://localhost:9363/grup/panel");
      setTable(response.data);
      setData(response.data);


      const postaKodListesi = response.data.map(item => item.group);
      const data=postaKodListesi.length;
      const seriesData = [{ name: "Group", data: [data] }];


      setSeries(seriesData);
      setSerie(serieData);

      // dizi state'ini güncelle
      setDizi([...seriesData, ...serieData]);

    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const [options, setOptions] = useState({
    chart: {
      id: "basic-bar"
    },
    xaxis: {
      categories: ["Electronic Care","Forklif", "Machine Care"] // Başlangıçta boş olarak ayarlayın, veriler geldiğinde güncellenecek
    }
  });



  return (
    <>
       <ReactApexChart          options={options}
              series={dizi} type="area" height={350} />

    </>
  );
};
export default Charts;
