import { useState, useEffect } from "react";
import { TodoType } from "./todo";
import ApexCharts from "apexcharts";
import axios from "axios";

const TableTwo = () => {
  const [data, setData] = useState<TodoType[]>([]);
  const [table, setTable] = useState<TodoType[]>([]);
  const fetchData = async () => {
    try {
       await axios.get("http://localhost:9363/post/panel")
       .then((response)=>{
        setTable(response.data);
        setData(response.data);

       });



      // response nesnesi veya kullaniciAd özelliği tanımlı mı diye kontrol et

    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);
  let TotalData: number = data.length;
  const options = {
    chart: {
      height: "100%",
      maxWidth: "100%",
      type: "area",
      fontFamily: "Inter, sans-serif",
      dropShadow: {
        enabled: false,
      },
      toolbar: {
        show: false,
      },
    },
    tooltip: {
      enabled: true,
      x: {
        show: false,
      },
    },
    fill: {
      type: "gradient",
      gradient: {
        opacityFrom: 0.55,
        opacityTo: 0,
        shade: "#1C64F2",
        gradientToColors: ["#1C64F2"],
      },
    },
    dataLabels: {
      enabled: false,
    },
    stroke: {
      width: 6,
    },
    grid: {
      show: false,
      strokeDashArray: 4,
      padding: {
        left: 2,
        right: 2,
        top: 0,
      },
    },
    series: [
      {
        name: "New users",
        data: data,
        color: "#1A56DB",
      },
    ],
    xaxis: {
      categories: [
        "01 February",
        "02 February",
        "03 February",
        "04 February",
        "05 February",
        "06 February",
        "07 February",
      ],
      labels: {
        show: false,
      },
      axisBorder: {
        show: false,
      },
      axisTicks: {
        show: false,
      },
    },
    yaxis: {
      show: false,
    },
  };

  useEffect(() => {
    const areaChartElement = document.getElementById("area-chart");
    if (areaChartElement && typeof ApexCharts !== "undefined") {
      const chart = new ApexCharts(areaChartElement, options);
      chart.render();
    }
  }, [options]);

  let totalDataCount: number = table.length;
  return (
    <>
      <div>
        <div className="max-w-sm w-full bg-white rounded-lg shadow dark:bg-gray-800 p-4 md:p-6">
          <div className="flex justify-between">
            <div>
              <h5 className="leading-none text-3xl font-bold text-gray-900 dark:text-white pb-2"></h5>
              <p className="text-base font-normal text-gray-500 dark:text-gray-400">
                Users this week 
              </p>
            </div>
            <div className="flex items-center px-2.5 py-0.5 text-base font-semibold text-green-500 dark:text-green-500 text-center">
           { totalDataCount}


              <svg
                className="w-3 h-3 ms-1"
                aria-hidden="true"
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                viewBox="0 0 10 14"
              >
                <path
                  stroke="currentColor"
                  stroke-linecap="round"
                  stroke-linejoin="round"
                  stroke-width="2"
                  d="M5 13V1m0 0L1 5m4-4 4 4"
                />
              </svg>
            </div>
          </div>
          <div id="area-chart"></div>
        </div>
      </div>
    </>
  );
};
export default TableTwo;
