import React,{useState} from 'react'

const Appy = () => {
    const [newTodo, setNewTodo] = useState({
        kullaniciAd: "",
        kullaniciSoyad: "",
        kullaniciEmail: "",
        yasadigiSehir: "",
        adress: "",
        city: "",
        province: "",
        postaCode: "",
        planing: null,
      });
      const handleInputChange = (event: any) => {
        const { name, value } = event.target;
        setNewTodo({ ...newTodo, [name]: value });
      };
      const handleCheckboxChange = async (e: any, id: number) => {
        const isChecked = e.target.checked;
        setCheckedItems((prevState) => ({
          ...prevState,
          [id]: isChecked,
        }));
        if (isChecked) {
          try {
            await axios.delete(`http://localhost:9363/post/panel/${id}`);
            // Silinen öğeyi filtreleyerek veriyi güncelle
            setData((prevTodos) => prevTodos.filter((todo) => todo._id !== id));
          } catch (error) {
            console.error("Veri alınırken hata oluştu:", error);
          }
        }
      };

  return (
    <div>
            <input
                    type="checkbox"
                    className={`mt-5 w-4 h-4 ring-gray-500 bg-gray-100 border-gray-800 rounded focus:ring-gray-500 dark:focus:ring-gray-600 dark:ring-offset-gray-700 dark:focus:ring-offset-gray-700 focus:ring-2 dark:bg-gray-600 dark:border-gray-500`}
