import React from "react";
import axios from "axios";
import { TodoType } from "./todo";
import TableTwo from "./TableTwo";
import { Fragment, useRef, useState, useEffect } from "react";
import { Dialog, Transition } from "@headlessui/react";
import { useNavigate } from "react-router-dom";

const Table = () => {
  const Navigate=useNavigate();
  const [isChecked, setIsChecked] = useState(false);
  const [newTodo, setNewTodo] = useState({
    kullaniciAd: "",
    kullaniciSoyad: "",
    kullaniciEmail: "",
    yasadigiSehir: "",
  });

  const handleInputChange = (e: any) => {
    setNewTodo({
      ...newTodo,
      [e.target.name]: e.target.value,
    });
  };
  const [data, setData] = useState<TodoType[]>([]);
  const [open, setOpen] = useState(true);

  // const cancelButtonRef =useRef<HTMLDivElement | null>(null);

  const fetchData = async () => {
    try {
      const response = await axios.get("http://localhost:9363/post/panel");
      setData(response.data.slice(0, 5)); // slice işlemini burada gerçekleştirin
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };
  useEffect(() => {
    fetchData();
  });


  const EventClick = async (e: any, id: any) => {
    e.preventDefault();
    const isChecked = e.target.checked;
    if (isChecked) {
      try {
        await axios.delete(`http://localhost:9363/post/panel/${id}`);
        // Silinen öğeyi filtreleyerek veriyi güncelle
        setData((prevTodos) => prevTodos.filter((todo) => todo._id !== id));
      } catch (error) {
        console.error("Veri alınırken hata oluştu:", error);
      }
    } else {
      // Checkbox işareti kaldırıldığında bir şey yapılabilir, burada örnek bir konsol çıktısı verilmiştir.
      console.log("Checkbox işareti kaldırıldı, id:", id);
    }
  };

  const Click = () => {
    setOpen(true);
  };
  const handleFormSubmit = async (e: any) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:9363/post/panel", newTodo).then(() => {
        setNewTodo({
          kullaniciAd: "",
          kullaniciSoyad: "",
          kullaniciEmail: "",
          yasadigiSehir: "",
        });
        console.log(newTodo)
      });
    } catch (error) {
      console.error("Failed to make POST request:", error);
    }
  };
  return (
    <>
      <div className="dark:bg-gray-800 w-100 h-8 col-span-5 ">
        <img
          src="./plus.png"
          width={30}
          height={30}
          className="float-right pr-2 cursor-pointer "
          onClick={Click}
        ></img>
      </div>
      <table className="w-full text-sm text-left rtl:text-right text-gray-500 dark:text-gray-800">
        <thead className="text-xs text-gray-700 uppercase bg-white  dark:bg-gray-800 dark:text-gray-400">
          <tr>
            <th scope="col" className="px-6 py-3">
              Delete
            </th>
            <th scope="col" className="px-6 py-3">
              Firstname
            </th>
            <th scope="col" className="px-6 py-3">
              Lastname
            </th>

            <th scope="col" className="px-6 py-3">
              Email
            </th>
            <th scope="col" className="px-6 py-3">
              Country
            </th>
            <th scope="col" className="px-6 py-3">
              Date
            </th>
            <th scope="col" className="px-6 py-3">
              Operations
            </th>
          </tr>
        </thead>
        <tbody>
          {data.map((item, index) => {
            return (
              <tr
                key={index}
                className="bg-white border-b dark:bg-gray-800 dark:border-gray-700"
              >
    <input
        id="vue-checkbox"
        type="checkbox"
        checked={isChecked}
        className={`mt-5 w-4 h-4 text-gray-700 bg-gray-100 border-gray-300 rounded focus:ring-gray-500 dark:focus:ring-gray-600 dark:ring-offset-gray-700 dark:focus:ring-offset-gray-700 focus:ring-2 dark:bg-gray-600 dark:border-gray-500`}
        onChange={(e) => {
          setIsChecked(e.target.checked); 
          EventClick(e, item._id);
        }}
      />



                <td
                  scope="row"
                  className="px-6 py-4 font-medium text-gray-900 whitespace-nowrap dark:text-white"
                >
                  {item.kullaniciAd}
                </td>
                <td
                  scope="row"
                  className="px-6 py-4 font-medium text-gray-900 whitespace-nowrap dark:text-white"
                >
                  {item.kullaniciSoyad}
                </td>
                <td
                  scope="row"
                  className="px-6 py-4 font-medium text-gray-900 whitespace-nowrap dark:text-white"
                >
                  {item.kullaniciEmail}
                </td>
                <td
                  scope="row"
                  className="px-6 py-4 font-medium text-gray-900 whitespace-nowrap dark:text-white"
                >
                  {item.yasadigiSehir}
                </td>
                <td
                  scope="row"
                  className="px-6 py-4 font-medium text-gray-900 whitespace-nowrap dark:text-white"
                >
                {new Date(item.createdAt).toLocaleString()}

                </td>
                <td className="row px-6 py-4 font-medium text-gray-900 whitespace-nowrap dark:text-white">

                  <img
                    src="./view.png"
                    width={20}
                    height={20}
                    className="float-left pr-2 cursor-pointer"
                    onClick={() => Navigate(`/detail/${item._id.slice(1, 3)}`)}



                  ></img>
                  <img
                    src="./edit.png"
                    width={20}
                    height={20}
                    className="cursor-pointer"
                  ></img>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
      <Transition.Root show={open} as={Fragment}>
        <Dialog
          as="div"
          className="relative z-10"
          // initialFocus={cancelButtonRef}
          onClose={setOpen}
        >
          <Transition.Child
            as={Fragment}
            enter="ease-out duration-300"
            enterFrom="opacity-0"
            enterTo="opacity-100"
            leave="ease-in duration-200"
            leaveFrom="opacity-100"
            leaveTo="opacity-0"
          >
            <div className="fixed inset-0 bg-gray-800 bg-opacity-75 transition-opacity" />
          </Transition.Child>

          <div className="fixed inset-0 z-10 w-screen overflow-y-auto ">
            <div className="flex min-h-full items-end justify-center p-4 text-center sm:items-center sm:p-0 ">
              <Transition.Child
                as={Fragment}
                enter="ease-out duration-300"
                enterFrom="opacity-0 translate-y-4 sm:translate-y-0 sm:scale-95"
                enterTo="opacity-100 translate-y-0 sm:scale-100"
                leave="ease-in duration-200"
                leaveFrom="opacity-100 translate-y-0 sm:scale-100"
                leaveTo="opacity-0 translate-y-4 sm:translate-y-0 sm:scale-95"
              >
                <Dialog.Panel className="relative transform overflow-hidden rounded-lg bg-gray-800 text-left shadow-xl transition-all sm:my-8 sm:w-full sm:max-w-lg">
                  <div className="bg-dark-800 px-4 pb-4 pt-5 sm:p-6 sm:pb-4">
                    <div className=" sm:items-stretch">
                      <div className="mt-3 text-center sm:ml-4 sm:mt-0 sm:text-left">
                        <div className="mt-2">
                          <form onSubmit={handleFormSubmit}>
                            <div className="space-y-12">
                              <div className="border-b border-gray-900/10 pb-12">
                                <h2 className="text-base font-semibold leading-7 text-white">
                                  Personal Information
                                </h2>
                                <p className="mt-1 text-sm leading-6 text-white">
                                  Use a permanent address where you can receive
                                  mail.
                                </p>

                                <div className="mt-10 grid grid-cols-1 gap-x-6 gap-y-8 sm:grid-cols-6">
                                  <div className="sm:col-span-3">
                                    <label
                                      htmlFor="first-name"
                                      className="block text-sm font-medium leading-6 text-white"
                                    >
                                      First name
                                    </label>
                                    <div className="mt-2">
                                      <input
                                        type="text"
                                        id="first-name"
                                        autoComplete="given-name"
                                        name="kullaniciAd"
                                        value={newTodo.kullaniciAd}
                                        onChange={handleInputChange}
                                        className="pl-1 block w-full rounded-md border-0 py-1.5 text-white shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
                                      />
                                    </div>
                                  </div>

                                  <div className="sm:col-span-3">
                                    <label
                                      htmlFor="last-name"
                                      className="block text-sm font-medium leading-6 text-white"
                                    >
                                      Last name
                                    </label>
                                    <div className="mt-2">
                                      <input
                                        type="text"
                                        name="kullaniciSoyad"
                                        value={newTodo.kullaniciSoyad}
                                        id="last-name"
                                        autoComplete="family-name"
                                        onChange={handleInputChange}
                                        className="pl-1 block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
                                      />
                                    </div>
                                  </div>

                                  <div className="sm:col-span-6">
                                    <label
                                      htmlFor="email"
                                      className=" block text-sm font-medium leading-6 text-white"
                                    >
                                      Email address
                                    </label>
                                    <div className="mt-2">
                                      <input
                                        id="email"
                                        name="kullaniciEmail"
                                        value={newTodo.kullaniciEmail}
                                        onChange={handleInputChange}
                                        type="email"
                                        autoComplete="email"
                                        className="pl-1 block w-full rounded-md border-0 py-1.5 text-white shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
                                      />
                                    </div>
                                  </div>

                                  <div className="sm:col-span-7">
                                    <label
                                      htmlFor="country"
                                      className="block text-sm font-medium leading-6 text-white"
                                    >
                                      Country
                                    </label>
                                    <div className="mt-2 ">
                                      <select
                                        id="country"
                                        name="yasadigiSehir"
                                        value={newTodo.yasadigiSehir}
                                        onChange={handleInputChange}
                                        autoComplete="country-name"
                                        className="pl-1 block w-full rounded-md border-0 py-1.5 text-white shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
                                      >
                                        <option value="United States">
                                          United States
                                        </option>
                                        <option value="Canada">Canada</option>
                                        <option value="Mexico">Mexico</option>
                                      </select>
                                    </div>
                                  </div>
                                </div>
                              </div>
                            </div>

                            <div className="mt-6 flex items-center justify-end gap-x-6">
                              <button
                                type="submit"
                                className="rounded-md bg-indigo-600 px-3 py-2 text-sm font-semibold text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600"
                              >
                                Save
                              </button>
                            </div>
                          </form>
                        </div>
                      </div>
                    </div>
                  </div>
                  <div className=" bg-gray-800 px-4 py-3 sm:flex sm:flex-row-reverse sm:px-6 float-end">
                    <button
                      type="button"
                      className="inline-flex w-full justify-center rounded-md bg-red-600 px-3 py-2 text-sm font-semibold text-white shadow-sm hover:bg-red-500 sm:ml-3 sm:w-auto"
                      onClick={() => setOpen(false)}
                    >
                      exit the screen
                    </button>
                  </div>
                </Dialog.Panel>
              </Transition.Child>
            </div>
          </div>
        </Dialog>
      </Transition.Root>
      <TableTwo data={data} />
    </>
  );
};

export default Table;
